import { Component, OnInit ,ViewChild} from '@angular/core';
import { DataService } from '../data.service';
import { Router } from '@angular/router';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-sections',
  templateUrl: './sections.component.html',
  styleUrls: ['./sections.component.scss']
})
export class SectionsComponent implements OnInit{
  @ViewChild(MatPaginator) paginator!: MatPaginator;
  dataSource!: MatTableDataSource<any>;
  data:any;
  sections:any;
  courseId:any;
  courseName:any;
  pagedCourses: any[] = [];
  pageSize = 10;
  pageIndex:number  =1
  constructor(private dataService: DataService, private route: ActivatedRoute) {}

  ngOnInit() {
    this.route.queryParams.subscribe(params => {
      if (params['id'] && params['name']) {
        this.courseId = JSON.parse(params['id']);
        this.courseName = params['name'];

        // Now 'this.courseId' and 'this.courseName' contain the string values
        console.log(this.courseName,this.courseId );
        const id= this.courseId

        // Continue with your code, e.g., making an API request
        this.getData(this.pageIndex);
      }
    }); 
  }
  getData(pageIndex:any) {

    const apiUrl = `http://localhost:3000/sections?pageNo=${this.pageIndex}&limit=${this.pageSize}&q=${id}`;
        this.dataService.getApiData(apiUrl).subscribe((data) => {
          console.log(data);
          this.data
          this.sections = data.results;
        });
  }
  onPageChange(event:any) {
    this.pageIndex = event.pageIndex +1;
    this.getData(this.pageIndex)
  }
}
<div class="courses ">
    <div class="color-overlay"></div>
    <div class="layer h-100  "></div>
        <div id="sections" class="pt-3 ps-5 position-relative">
            <h2 class="pt-5">Course Sections</h2>
            <div class="container ">

                <div *ngFor="let section of sections" class="section shadow-lg position-relative">
                    <h3>{{courseName}}</h3>
                    <div class="d-flex justify-content-start">
                      <p>Subtype: {{section.section_type}}</p>
                      <p>Section: {{section.section_name}}</p>
                    </div>
                    <p>{{section.section_day}}</p>
                    <div class="time d-flex justify-content-between">
                      <p>{{section.section_from}} - {{section.section_to}}</p>

                    </div>
                    <div class="instructor text-center">
                        <i class="bi bi-person-circle"></i>
                        <br>
                        <p class="mt-3">{{section.instructor_username}}</p>
                    </div>



                </div>







            </div>
            <a class="backbutton" href="/courses">
                <div class="btn  btn-dark" >Back to Courses</div>
            </a>
          </div>


          <mat-paginator
          [length]="data?.pagination.totalNumber"
          [pageSize]="10"

          (page)="onPageChange($event)"
  ></mat-paginator>
</div>
