import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { DataService } from '../data.service';
@Component({
  selector: 'app-sections',
  templateUrl: './sections.component.html',
  styleUrls: ['./sections.component.scss']
})
export class SectionsComponent implements OnInit{
  sections:any;
  courseId:any;
  courseName:any;
  constructor(private dataService: DataService, private route: ActivatedRoute) {}

  ngOnInit() {
    this.route.paramMap.subscribe(params => {
      this.courseId = params.get('id');
      this.courseName=params.get('name');
      console.log(this.courseId, this.courseName)
      const apiUrl = `http://localhost:3000/sections/${this.courseId}`;       
      this.dataService.getApiData(apiUrl).subscribe((data) => {
      console.log(data);
      this.sections=data.results;

    });
    })

  }
}
<div class="courses ">
    <div class="color-overlay"></div>
    <div class="layer h-100  "></div>
        <div id="sections" class="pt-3 ps-5 position-relative">
            <h2 class="pt-5">Course Sections</h2>
            <div class="container ">

                <div *ngFor="let section of sections" class="section shadow-lg position-relative">
                    <h3>{{courseName}}</h3>
                    <div class="d-flex justify-content-start">
                      <p>Subtype: {{section.section_type}}</p>
                      <p>Section: {{section.section_name}}</p>
                    </div>
                    <p>{{section.section_day}}</p>
                    <div class="time d-flex justify-content-between">
                      <p>{{section.section_from}} - {{section.section_to}}</p>

                    </div>
                    <div class="instructor text-center">
                        <i class="bi bi-person-circle"></i>
                        <br>
                        <p class="mt-3">{{section.instructor_username}}</p>
                    </div>



                </div>







            </div>
            <a class="backbutton" href="/courses">
                <div class="btn  btn-dark" >Back to Courses</div>
            </a>
          </div>



</div>
