import { Component, OnInit } from '@angular/core';
import { DataService } from '../data.service';
@Component({
  selector: 'app-courses',
  templateUrl: './courses.component.html',
  styleUrls: ['./courses.component.scss']
})
export class CoursesComponent implements OnInit {
  inputValue: string = '';
  value:any;
  courses:any;
  refCourses:any;
  constructor(private dataService: DataService) { }
  ngOnInit(): void {
    const apiUrl = `http://localhost:3000/courses?pageNo=1&limit=20&q=${this.inputValue}`;       
    this.dataService.getApiData(apiUrl).subscribe((data) => {
      console.log(data);
      this.courses=data.results;
      this.refCourses=data;
    });
  }
  searchCourses(){
    const apiUrl = `http://localhost:3000/courses?pageNo=1&limit=20&q=${this.inputValue}`;       
    this.dataService.getApiData(apiUrl).subscribe((data) => {
      console.log(data);
      this.courses=data.results;
      this.refCourses=data;
    });
  }
  addCourse


}
<div class="courses">
    <div class="color-overlay"></div>
    <div class="layer h-100"></div>
        <div id="courses" class=" p-5" >

            <h2 class="pt-5">Courses</h2>
            <div class="container">
                <div name="inputValue" ngDefaultControl [(ngModel)]="inputValue" (keyup)="searchCourses()" class="search position-relative mb-5">
                    <input type="text" class="w-100 h-100" placeholder="Find your courses ... " value="">
                    <button class="bg-info" >Find</button>
                </div>
                <div *ngFor="let course of courses; let i = index" class="course shadow-lg">
                    <h3>{{course.course_code}}</h3>
                    <p>{{course.course_name}}</p>
                    <div class="btns d-flex">
                        <button class="btn addBtn" (click)="addCourse(i)">ADD</button>
                        <button class="btn viewBtn">VIEW</button>
                    </div>   
                </div>

            </div>

        </div>

</div>
